mapper.select:
select aj.*,lu.name as lawername,lu.position from anjian aj left join laweruser lu on aj.lawerid =lu.id
where aj.business_id=#{map.busId}

            and aj.name like concat('%',#{map.name},'%')


            and aj.lawerid = #{map.lawerid}


            and aj.p_status = #{map.pstatus}


            AND unix_timestamp(aj.rtime) between unix_timestamp(#{map.tricktime}) and
            unix_timestamp(#{map.tricktime2})

        order by aj.rtime desc

            LIMIT #{map.skipCount},#{map.pageSize}

select count(1) from anjian aj
 where aj.business_id=#{map.busId}

     and aj.name like concat('%',#{map.name},'%')


     and aj.lawerid = #{map.lawerid}


     and aj.p_status = #{map.pstatus}


     AND unix_timestamp(aj.rtime) between unix_timestamp(#{map.tricktime}) and
     unix_timestamp(#{map.tricktime2})

select aj.*,lu.name as lawername,lu.position from anjian aj left join laweruser lu on aj.lawerid =lu.id where aj.business_id=#{map.busId} and aj.lawerid = #{map.lawerid}
aj.name like concat('%',#{map.name},'%') OR aj.cusname like concat('%',#{map.name},'%')
and aj.p_status = #{map.pstatus}
AND unix_timestamp(aj.rtime) between unix_timestamp(#{map.tricktime}) and unix_timestamp(#{map.tricktime2})
AND unix_timestamp(aj.ftime) between unix_timestamp(#{map.tricktime}) and unix_timestamp(#{map.tricktime2})
order by aj.rtime desc
LIMIT #{map.skipCount},#{map.pageSize}

select count(1) from anjian aj where aj.business_id=#{map.busId} and aj.lawerid = #{map.lawerid}
aj.name like concat('%',#{map.name},'%') OR aj.cusname like concat('%',#{map.name},'%')
and aj.p_status = #{map.pstatus}
AND unix_timestamp(aj.rtime) between unix_timestamp(#{map.tricktime}) and unix_timestamp(#{map.tricktime2})
AND unix_timestamp(aj.ftime) between unix_timestamp(#{map.tricktime}) and unix_timestamp(#{map.tricktime2})
